mapper.select:

select s.*, c.name categoryName from setmeal s left join
category c on s.category_id = c.id


        and s.name like concat('%',#{name},'%')


        and s.category_id = #{categoryId}


        and s.status = #{status}

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
